import React from 'react';

const Player = ({ playlistId, name, color }) => {
  return (
    <div style={{...styles.container, backgroundColor: color}}>
      <div style={styles.header}>
        <h1 style={styles.title}>BREWBELLY.FM</h1>
        <h2 style={styles.subtitle}></h2>
      </div>
      <div style={styles.player}>
        <div style={styles.songInfo}>
          <h2 style={styles.songTitle}></h2>
          <h3 style={styles.songArtist}></h3>
        </div>
        <audio src=""></audio>
        <div style={styles.songCount}></div>
        <div style={styles.controls}>
          <div style={styles.fas} id="prev-btn">back</div>
          <div style={styles.fas} id="play-btn">play</div>
          <div style={styles.fas} id="next-btn">fwrd</div>
        </div>
        <div style={styles.timelineContainer}>
          <div style={styles.progressBar}></div>
          <div style={styles.timeInfo}></div>
        </div>
      </div>
    </div>
  )
}

const styles = {
  container: {
    borderStyle: 'solid',
    borderWidth: '1px',
    padding: '16px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  header: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    margin: '16px',
  },
  title: {
    fontSize: '16px',
    lineHeight: '19px',
    fontWeight: 400,
    margin: '0 0 16px',
  },
  subtitle: {
    fontSize: '24px',
    lineHeight: '29px',
    margin: 0,
  },
  player: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  songInfo: {
    margin: '16px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  songTitle: {
    fontSize: '16px',
    lineHeight: '19px',
    fontWeight: 400,
    margin: 0,
  },
  songArtist: {
    fontSize: '16px',
    lineHeight: '19px',
    fontWeight: 400,
    margin: 0,
  },
  songCount: {
    margin: '16px',
  },
  controls: {
    display: 'flex',
    flexDirection: 'row',
    margin: '16px',
  },
  fas: {
    margin: '0 8px',
  },
  timelineContainer: {
    margin: '16px',
  },

}

export default Player
