import React, { useState, useEffect, useRef } from 'react';

const Player = ({ playlistId, name, color }) => {
  const [songList, setSongList] = useState([])
  const [isPlaying, setIsPlaying] = useState(true)
  const [currentSongIndex, setCurrentSongIndex] = useState(0)
  const [currentSong, setCurrentSong] = useState('')
  const [currentArtist, setCurrentArtist] = useState('')
  const [currentUrl, setCurrentUrl] = useState('')
  const [songCount, setSongCount] = useState('0 / 0')
  const [progress, setProgress] = useState(0)
  const [timeInfo, setTimeInfo] = useState('0:00 / 0:00')
  const [loading, setLoading] = useState(false)

  const YOUTUBE_API_KEY = 'AIzaSyA8NmqtBGCc12OcuVhZUDNhzTYbagkoHTk';

  const audioRef = useRef()
  const progressBarRef = useRef()

  // Retrieve playlist items from YouTube API
  const getPlaylistItems = () => {
    const response = fetch(`https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&maxResults=50&playlistId=${playlistId}&key=${YOUTUBE_API_KEY}`)
    const data = response.json()
    return data.items
  }

  // Map playlist items to song objects
  const createSongList = async () => {
    const items = getPlaylistItems()
    return items.map((item, index) => ({
      title: item.snippet.title,
      artist: item.snippet.videoOwnerChannelTitle,
      url: `https://www.youtube.com/watch?v=${item.snippet.resourceId.videoId}`,
      id: index + 1
    }))
  }

  // Update the player with the current song's information
  const updatePlayer = async () => {
    setCurrentSong(songList[currentSongIndex])
    setCurrentArtist(currentSong.artist)
    setCurrentUrl(currentSong.url)
    setSongCount(`${currentSongIndex + 1} / ${songList.length}`)
    setTimeInfo(`${audioRef.current.currentTime} / ${audioRef.current.duration}`)
  }

  // Toggle the play/pause state of the audio element
  const togglePlay = () => {
    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }

    setIsPlaying((prev) => !prev)
  }

  // Skip to the next song in the playlist
  const skipForward = () => {
    setCurrentSongIndex((currentSongIndex + 1) % songList.length)
    updatePlayer()
    audioRef.current.play()
  }

  // Skip to the previous song in the playlist
  const skipBack = () => {
    setCurrentSongIndex((currentSongIndex - 1 + songList.length) % songList.length)
    updatePlayer()
    audioRef.current.play()
  }

  // Update the progress bar to match the current time of the audio element
  const updateProgressBar = () => {
    setProgress((audioRef.current.currentTime / audioRef.current.duration) * 100)
    setTimeInfo(`${audioRef.current.currentTime} / ${audioRef.current.duration}`)
  }

  // Scrub the audio element to the location of the mouse click on the progress bar
  const scrub = (e) => {
    const scrubTime = (e.offsetX / progressBarRef.offsetWidth) * audioRef.current.duration;
    audioRef.current.currentTime = scrubTime;
  }

  useEffect(() => {
    setSongList(createSongList())
    updatePlayer()
  })

  if (loading) <h1>Loading...</h1>

  return (
    <div style={{...styles.container, backgroundColor: color}}>
      <div style={styles.header}>
        <h1 style={styles.title}>BREWBELLY.FM</h1>
        <h2 style={styles.subtitle}>{name}</h2>
      </div>
      <div style={styles.player}>
        <div style={styles.songInfo}>
          <h2 style={styles.songTitle}></h2>
          <h3 style={styles.songArtist}></h3>
        </div>
        <audio src={currentUrl} ref={audioRef}></audio>
        <div style={styles.songCount}></div>
        <div style={styles.controls}>
          <button onClick={skipBack} style={styles.fas} id="prev-btn">back</button>
          <button onClick={togglePlay} style={styles.fas} id="play-btn">{isPlaying ? 'pause' : 'play'}</button>
          <button onClick={skipForward} style={styles.fas} id="next-btn">fwrd</button>
        </div>
        <div style={styles.timelineContainer}>
          <div ref={progressBarRef} style={{...styles.progressBar, width: `${progress}%`}}></div>
          <div style={styles.timeInfo}></div>
        </div>
      </div>
    </div>
  )
}

const styles = {
  container: {
    borderStyle: 'solid',
    borderWidth: '1px',
    padding: '16px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  header: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    margin: '16px',
  },
  title: {
    fontSize: '16px',
    lineHeight: '19px',
    fontWeight: 400,
    margin: '0 0 16px',
  },
  subtitle: {
    fontSize: '24px',
    lineHeight: '29px',
    fontWeight: 400,
    margin: 0,
  },
  player: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  songInfo: {
    margin: '16px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  songTitle: {
    fontSize: '16px',
    lineHeight: '19px',
    fontWeight: 400,
    margin: 0,
  },
  songArtist: {
    fontSize: '16px',
    lineHeight: '19px',
    fontWeight: 400,
    margin: 0,
  },
  songCount: {
    margin: '16px',
  },
  controls: {
    display: 'flex',
    flexDirection: 'row',
    margin: '16px',
  },
  fas: {
    margin: '0 8px',
  },
  timelineContainer: {
    margin: '16px',
  },

}

export default Player
