import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'
import { HomePage, BrewPage, BrewsPage } from './pages'
import firebase from './firebase'

const App = () => {
  const [brews, setBrews] = useState([])
  const [latestPlaylistId, setLatestPlaylistId] = ('')
  const [latestName, setLatestName] = ('')
  const [latestColor, setLatestColor] = ('#ffffff')
  const [loading, setLoading] = useState(false)

  const ref = firebase.firestore().collection('projects')

  const getProjects = () => {
    setLoading(true)

    ref.onSnapshot((querySnapshot) => {
      const projects = []
      querySnapshot.forEach((doc) => {
        projects.push(doc.data())
      })

      setProjects(projects)
      setLoading(false)
    })
  }

  useEffect(() => {
    getProjects()
  })

  if (loading) <h1>Loading...</h1>

  return (
    <Router>
      <Routes>
        <Route path='/' element={<HomePage playlistId={latestPlaylistId} name={latestName} color={latestColor} />} />
        <Route path='/:brew' element={<BrewPage />} />
        <Route path='/:brews' element={<BrewsPage brews={brews} />} />
      </Routes>
    </Router>
  )
}

export default App
