import React, { useState, useEffect } from 'react'
import axios from 'axios'

const Player = ({ playlistId, name, color }) => {
  const [playlist, setPlaylist] = useState([])
  const [currentTrack, setCurrentTrack] = useState(0)
  const [isPlaying, setIsPlaying] = useState(false)
  const [trackProgress, setTrackProgress] = useState(0)

  const YOUTUBE_API_KEY = 'AIzaSyB7fFZ0tKyzlfUa1EJkO4iMhVqnmfFMzYE'

  useEffect(() => {
    axios.get(`https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&maxResults=25&playlistId=PLGL1RS_tj0UH6RRpimrDTXc9tP8FIx8LQ&key=${YOUTUBE_API_KEY}`)
      .then(res => {
        const tracks = res.data.items.map(item => ({
          id: item.snippet.resourceId.videoId,
          title: item.snippet.title,
          artist: item.snippet.channelTitle
        }))
        setPlaylist(tracks)
      })
      .catch(err => console.log(err))
  }, [])

  useEffect(() => {
    const audio = document.getElementById('audio')
    if (isPlaying) {
      audio.play()
    } else {
      audio.pause()
    }
  }, [isPlaying])

  useEffect(() => {
    const audio = document.getElementById('audio')
    audio.currentTime = trackProgress
  }, [trackProgress])


}

const styles = {
  container: {
    borderStyle: 'solid',
    borderWidth: '1px',
    padding: '16px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  header: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    margin: '16px',
  },
  title: {
    fontSize: '16px',
    lineHeight: '19px',
    fontWeight: 400,
    margin: '0 0 16px',
  },
  subtitle: {
    fontSize: '24px',
    lineHeight: '29px',
    fontWeight: 400,
    margin: 0,
  },
  player: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  songInfo: {
    margin: '16px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  songTitle: {
    fontSize: '16px',
    lineHeight: '19px',
    fontWeight: 400,
    margin: 0,
  },
  songArtist: {
    fontSize: '16px',
    lineHeight: '19px',
    fontWeight: 400,
    margin: 0,
  },
  songCount: {
    margin: '16px',
  },
  controls: {
    display: 'flex',
    flexDirection: 'row',
    margin: '16px',
  },
  fas: {
    background: 'none',
    border: 'none',
    padding: 0,
    fontFamily: 'monospace',
    fontSize: '16px',
    lineHeight: '19px',
    textTransform: 'uppercase',
    margin: '0 8px',
  },
  timelineContainer: {
    margin: '16px',
  },

}

export default Player
