import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'
import { HomePage, BrewPage, BrewsPage } from './pages'
import firebase from './firebase'

const App = () => {
  const [projects, setProjects] = useState([])
  const [loading, setLoading] = useState(false)

  const ref = firebase.firestore().collection('projects')

  const getProjects = () => {
    setLoading(true)

    ref.onSnapshot((querySnapshot) => {
      const projects = []
      querySnapshot.forEach((doc) => {
        projects.push(doc.data())
      })

      setProjects(projects)
      setLoading(false)
    })
  }

  useEffect(() => {
    getProjects()
  })

  if (loading) <h1>Loading...</h1>

  return (
    <Router>
      <Routes>
        <Route path='/' element={<HomePage loading={false} projects={projects} />} />
        <Route path='/:brew' element={<BrewPage projects={projects} />} />
        <Route path='/:brews' element={<BrewsPage />} />
      </Routes>
    </Router>
  )
}

export default App
