import React, { useState, useEffect } from 'react';

const Player = ({ playlistId, name, color }) => {
  const [songList, setSongList] = useState([])
  const [currentSongIndex, setCurrentSongIndex] = useState(0)
  const [currentSong, setCurrentSong] = useState('')
  const [currentArtist, setCurrentArtist] = useState('')
  const [currentUrl, setCurrentUrl] = useState('')
  const [songCount, setSongCount] = useState('0 / 0')
  const [timeInfo, setTimeInfo] = useState('0:00 / 0:00')
  const [loading, setLoading] = useState(false)

  const YOUTUBE_API_KEY = 'AIzaSyA8NmqtBGCc12OcuVhZUDNhzTYbagkoHTk';

  // Retrieve playlist items from YouTube API
  const getPlaylistItems = () => {
    const response = fetch(`https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&maxResults=50&playlistId=${playlistId}&key=${YOUTUBE_API_KEY}`)
    const data = response.json()
    return data.items
  }

  // Map playlist items to song objects
  const createSongList = () => {
    const items = getPlaylistItems()
    return items.map((item, index) => ({
      title: item.snippet.title,
      artist: item.snippet.videoOwnerChannelTitle,
      url: `https://www.youtube.com/watch?v=${item.snippet.resourceId.videoId}`,
      id: index + 1
    }))
  }

  // Get references to player elements
  const audio = document.querySelector('.audio');
  const playBtn = document.querySelector('.play-btn');
  const forwardBtn = document.getElementById('next-btn');
  const backBtn = document.getElementById('prev-btn');
  const progressBar = document.querySelector('.progress-bar');

  // Update the player with the current song's information
  const updatePlayer = () => {
    setCurrentSong(songList[currentSongIndex])
    setCurrentArtist(currentSong.artist)
    setCurrentUrl(currentSong.url)
    setSongCount(`${currentSongIndex + 1} / ${songList.length}`)
    setTimeInfo(`${audio.currentTime} / ${audio.duration}`)
  }

  // Toggle the play/pause state of the audio element
  const togglePlay = () => {
    if (audio.paused) {
      audio.play();
    } else {
      audio.pause();
    }
  }

  // Skip to the next song in the playlist
  const skipForward = () => {
    setCurrentSongIndex((currentSongIndex + 1) % songs.length)
    updatePlayer()
    audio.play()
  }

  // Skip to the previous song in the playlist
  const skipBack = () => {
    setCurrentSongIndex((currentSongIndex - 1 + songs.length) % songs.length)
    updatePlayer()
    audio.play()
  }

  // Update the progress bar to match the current time of the audio element
  const updateProgressBar = () => {
    const progress = (audio.currentTime / audio.duration) * 100;
    progressBar.style.width = `${progress}%`;
    timeInfo.innerText = `${audio.currentTime} / ${audio.duration}`;
  }

  // Scrub the audio element to the location of the mouse click on the progress bar
  const scrub = (e) => {
    const scrubTime = (e.offsetX / progressBar.offsetWidth) * audio.duration;
    audio.currentTime = scrubTime;
  }

  // Initialize the player with the first song in the playlist when the page loads
  window.addEventListener('load', async () => {
    songs = await createSongList();
    updatePlayer();
  });

  // Add event listeners to player elements
  playBtn.addEventListener('click', togglePlay);
  forwardBtn.addEventListener('click', skipForward);
  backBtn.addEventListener('click', skipBack);
  audio.addEventListener('timeupdate', updateProgressBar);
  progressBar.addEventListener('click', scrub);

  useEffect(() => {
    setSongList(createSongList())
    updatePlayer()
  })

  if (loading) <h1>Loading...</h1>

  return (
    <div style={{...styles.container, backgroundColor: color}}>
      <div style={styles.header}>
        <h1 style={styles.title}>BREWBELLY.FM</h1>
        <h2 style={styles.subtitle}>{name}</h2>
      </div>
      <div style={styles.player}>
        <div style={styles.songInfo}>
          <h2 style={styles.songTitle}></h2>
          <h3 style={styles.songArtist}></h3>
        </div>
        <audio src=""></audio>
        <div style={styles.songCount}></div>
        <div style={styles.controls}>
          <div style={styles.fas} id="prev-btn">back</div>
          <div style={styles.fas} id="play-btn">play</div>
          <div style={styles.fas} id="next-btn">fwrd</div>
        </div>
        <div style={styles.timelineContainer}>
          <div style={styles.progressBar}></div>
          <div style={styles.timeInfo}></div>
        </div>
      </div>
    </div>
  )
}

const styles = {
  container: {
    borderStyle: 'solid',
    borderWidth: '1px',
    padding: '16px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  header: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    margin: '16px',
  },
  title: {
    fontSize: '16px',
    lineHeight: '19px',
    fontWeight: 400,
    margin: '0 0 16px',
  },
  subtitle: {
    fontSize: '24px',
    lineHeight: '29px',
    fontWeight: 400,
    margin: 0,
  },
  player: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  songInfo: {
    margin: '16px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  songTitle: {
    fontSize: '16px',
    lineHeight: '19px',
    fontWeight: 400,
    margin: 0,
  },
  songArtist: {
    fontSize: '16px',
    lineHeight: '19px',
    fontWeight: 400,
    margin: 0,
  },
  songCount: {
    margin: '16px',
  },
  controls: {
    display: 'flex',
    flexDirection: 'row',
    margin: '16px',
  },
  fas: {
    margin: '0 8px',
  },
  timelineContainer: {
    margin: '16px',
  },

}

export default Player
